mapper.insert:
insert into blogs (uid, content, title, cover, createday)
values (#{blog.uid}, #{blog.content}, #{blog.title}, #{blog.cover},
        DATE_FORMAT(now(), '%Y-%m-%d'));

mapper.update:
update blogs
    -- 其实可以添加个updateday来记录更新时间
set content = #{blog.content},
    title= #{blog.title},
    cover= #{blog.cover}
where id = #{blog.id};
